($=>{$.fn.adjustTextToBackground=function(config){const options=$.extend({lightness:.6},config);return $(this).each((index,element)=>{const backgroundColor=$(element).css("background-color").replace("rgb(","").replace(")","").split(",");const[R,G,B]=backgroundColor;const rPrime=R/255;const gPrime=G/255;const bPrime=B/255;const cMax=Math.max(rPrime,gPrime,bPrime);const cMin=Math.min(rPrime,gPrime,bPrime);const lightness=(cMax+cMin)/2;lightness>=options.lightness?$(element).css("color","black"):$(element).css("color","white")})}})(jQuery);